+        var hour = min / 60;
+        var day = hour / 24;
+
+        var timeString = '';
+        if (day >= 1) {
+            timeString = Math.round(day) + ' days ago';
+        } else if (hour >= 1) {
+            timeString = Math.round(hour) + ' hours ago';
+        } else if (min >= 1) {
+            timeString = Math.round(min) + ' minutes ago';
+        } else {
+            timeString = Math.round(sec) + ' seconds ago';
+        }
+
+        return timeString;
+    }
